import React, { useState, useEffect } from "react";
import FullCalendar from "@fullcalendar/react";
import dayGridPlugin from "@fullcalendar/daygrid";
import timeGridPlugin from "@fullcalendar/timegrid";
import interactionPlugin from "@fullcalendar/interaction";
import BookingForm from "./BookingForm/BookingForm";
import "./Calendar.css";

export default function Calendar() {
  const [events, setEvents] = useState([]);
  const [locations, setLocations] = useState([]);
  const [teamMembers, setTeamMembers] = useState([]);
  const [services, setServices] = useState([]);

  const [showBookingForm, setShowBookingForm] = useState(false);
  const [selectedSlot, setSelectedSlot] = useState(null);
  const [customerId, setCustomerId] = useState("");
  const [selectedLocation, setSelectedLocation] = useState("");
  const [selectedTeamMember, setSelectedTeamMember] = useState("");
  const [selectedService, setSelectedService] = useState("");

  const [sidebarDay, setSidebarDay] = useState(new Date());
  const [availableSlots, setAvailableSlots] = useState([]);

  // load backend data
  useEffect(() => {
    async function loadData() {
      try {
        const [locRes, teamRes, serviceRes, bookingRes] = await Promise.all([
          fetch("http://localhost:4000/api/locations").then((r) => r.json()),
          fetch("http://localhost:4000/api/team-members").then((r) => r.json()),
          fetch("http://localhost:4000/api/services").then((r) => r.json()),
          fetch("http://localhost:4000/api/bookings").then((r) => r.json()),
        ]);

        setLocations(Array.isArray(locRes) ? locRes : []);
        setTeamMembers(
          teamRes?.team_members || [
            {
              id: "test1",
              team_member_details: { given_name: "Lucy", family_name: "Oh" },
            },
          ]
        );
        setServices(
          serviceRes?.objects || [
            { id: "srv1", item_data: { name: "Manicure" } },
            { id: "srv2", item_data: { name: "Pedicure" } },
            { id: "srv3", item_data: { name: "Nail Art" } },
            { id: "srv4", item_data: { name: "Gel Nails" } },
            { id: "srv5", item_data: { name: "Acrylic Nails" } },
          ]
        );
        setEvents(
          Array.isArray(bookingRes)
            ? bookingRes.map((b) => ({
                id: b.id,
                title: b.customer?.display_name || "Booking",
                start: b.start_at,
                end: b.end_at,
                team_member_id: b.team_member_id || null,
              }))
            : []
        );
      } catch (err) {
        console.error("Failed to load data:", err);
        alert("Error loading data â€” check console");
      }
    }

    loadData();
  }, []);

  // available slots sidebar
  useEffect(() => {
    if (!selectedTeamMember) {
      setAvailableSlots([]);
      return;
    }

    const slots = [];
    for (let hour = 9; hour < 17; hour++) {
      const start = new Date(sidebarDay);
      start.setHours(hour, 0, 0, 0);
      const end = new Date(sidebarDay);
      end.setHours(hour + 1, 0, 0, 0);

      const conflict = events.some(
        (e) =>
          e.team_member_id === selectedTeamMember &&
          new Date(e.start) < end &&
          new Date(e.end) > start
      );

      if (!conflict) slots.push({ start, end });
    }
    setAvailableSlots(slots);
  }, [events, sidebarDay, selectedTeamMember]);

  const handleDateSelect = (selectInfo) => {
    const staffConflict = events.some(
      (e) =>
        e.team_member_id === selectedTeamMember &&
        new Date(selectInfo.start) < new Date(e.end) &&
        new Date(selectInfo.end) > new Date(e.start)
    );

    if (staffConflict) {
      alert("This slot is already booked for the selected staff.");
      return;
    }

    setSelectedSlot({ start: selectInfo.startStr, end: selectInfo.endStr });
    setShowBookingForm(true);
  };

  const createBooking = async () => {
    if (
      !customerId ||
      !selectedLocation ||
      !selectedTeamMember ||
      !selectedService
    ) {
      return alert("Please fill all fields!");
    }

    const payload = {
      booking: {
        customer_id: customerId,
        location_id: selectedLocation,
        start_at: selectedSlot.start,
        end_at: selectedSlot.end,
        appointment_segments: [
          {
            team_member_id: selectedTeamMember,
            service_variation_id: selectedService,
            service_variation_version: 1,
          },
        ],
      },
    };

    try {
      const res = await fetch("http://localhost:4000/api/bookings", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(payload),
      });

      if (res.status === 409) return alert("This time slot is already booked.");

      const data = await res.json();
      if (data.id) {
        setEvents((prev) => [
          ...prev,
          {
            id: data.id,
            title: data.customer?.display_name || "Booking",
            start: data.start_at,
            end: data.end_at,
            team_member_id: selectedTeamMember,
          },
        ]);
        setShowBookingForm(false);
        setCustomerId("");
        setSelectedLocation("");
        setSelectedTeamMember("");
        setSelectedService("");
        setSelectedSlot(null);
        alert("Booking created âœ…");
      } else {
        alert("Failed to create booking â€” check console");
      }
    } catch (err) {
      console.error(err);
      alert("Error creating booking â€” check console");
    }
  };

  return (
    <div className="calendar-container">
      {/* Calendar & Sidebar on the left */}
      <div className="calendar-left">

        {/* Calendar */}
        <div className="booking-calendar">
          <h3>ðŸ“… Select Date & Time</h3>
          <p>Choose your preferred appointment date and available time slot.</p>

          <div className="calendar-box">
            <FullCalendar
              plugins={[dayGridPlugin, interactionPlugin]}
              initialView="dayGridMonth"
              selectable={true}
              events={events}
              select={handleDateSelect}
              headerToolbar={{
                left: "prev",
                center: "title",
                right: "next",
              }}
              height="auto"
            />
          </div>

          <div className="legend">
            <span className="dot available"></span> Available
            <span className="dot unavailable"></span> Unavailable
          </div>
        </div>

        <div className="calendar-sidebar">
  <h3>ðŸ“… Select Date & Time</h3>
  <p>Choose your preferred appointment date and available time slot.</p>

  {/* Date Picker Calendar (Month View) */}
  <div className="mini-calendar">
    <FullCalendar
      plugins={[dayGridPlugin, interactionPlugin]}
      initialView="dayGridMonth"
      selectable={true}
      select={(info) => setSidebarDay(new Date(info.startStr))}
      headerToolbar={{
        left: "prev",
        center: "title",
        right: "next",
      }}
      height="auto"
      dayMaxEvents={true}
    />
  </div>

  {/* Time Slots */}
  <div className="time-slots">
    {availableSlots.length ? (
      availableSlots.map((slot) => (
        <button
          className="time-slot-btn"
          key={slot.start.toISOString()}
          onClick={() => {
            setSelectedSlot({
              start: slot.start.toISOString(),
              end: slot.end.toISOString(),
            });
            setShowBookingForm(true);
          }}
        >
          {slot.start.toLocaleTimeString([], {
            hour: "2-digit",
            minute: "2-digit",
          })}{" "}
          -{" "}
          {slot.end.toLocaleTimeString([], {
            hour: "2-digit",
            minute: "2-digit",
          })}
        </button>
      ))
    ) : (
      <p>No available slots for this day</p>
    )}
  </div>

  {/* Legend */}
  <div className="legend">
    <span className="dot available"></span> Available
    <span className="dot unavailable"></span> Unavailable
  </div>
</div>

      </div>

      {/* Booking form on the right */}
      <div className="calendar-right">
        {showBookingForm && (
          <BookingForm
            customerId={customerId}
            setCustomerId={setCustomerId}
            selectedLocation={selectedLocation}
            setSelectedLocation={setSelectedLocation}
            selectedTeamMember={selectedTeamMember}
            setSelectedTeamMember={setSelectedTeamMember}
            selectedService={selectedService}
            setSelectedService={setSelectedService}
            locations={locations}
            teamMembers={teamMembers}
            services={services}
            onSubmit={createBooking}
            onCancel={() => setShowBookingForm(false)}
          />
        )}
      </div>
    </div>
  );
}
