const API_BASE = "http://localhost:3001/api";

async function fetchData(endpoint) {
  try {
    const res = await fetch(`${API_BASE}/${endpoint}`);
    if (!res.ok) throw new Error(`Failed to fetch ${endpoint}`);
    return await res.json();
  } catch (err) {
    console.error(err);
    return null;
  }
}

export async function getLocations() {
  const data = await fetchData("locations");
  if (data?.locations) return data.locations; // if wrapped
  return Array.isArray(data) ? data : [];
}

export async function getTeamMembers() {
  const data = await fetchData("team-members");
  return data?.team_members || []; // ✅ extract the array
}

export async function getServices() {
  const data = await fetchData("services");
  return data?.objects || []; // ✅ extract the array
}

export async function getBookings() {
  const data = await fetchData("bookings");
  return Array.isArray(data) ? data : [];
}



export async function createBooking(payload) {
  try {
    const res = await fetch(`${API_BASE}/bookings`, {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(payload),
    });
    if (!res.ok) throw new Error("Failed to create booking");
    return await res.json();
  } catch (err) {
    console.error(err);
    return null;
  }
}
